mapper.update:
UPDATE	auction
SET	a_hits = (	SELECT a_hits
					 	FROM auction
					 	WHERE a_num = #{a_num}
				)+1
WHERE a_num = #{a_num}

mapper.select:
SELECT *,
( 6371 * acos ( cos ( radians(#{lat}) ) * cos( radians( ad_lat ) ) * cos( radians( ad_lon ) - radians(#{lon}) ) + sin ( radians(#{lat}) ) * sin( radians( ad_lat ) ) )
) AS distance FROM auction a LEFT JOIN address b ON b.a_num = a.a_num LEFT JOIN auc_and_pro c ON c.a_num = a.a_num LEFT JOIN product d ON d.p_num = c.p_num LEFT JOIN picture e ON e.p_num = d.p_num WHERE a.a_sale = 0 GROUP BY a.a_num ORDER BY distance ASC, a.a_hits DESC LIMIT #{limit}, 6

mapper.update:
update auction
set a_sale = (	select a_sale
				from auction 
				where a_num = #{a_num}	
			)+1	
where a_num = #{a_num}
